exports.sendMail = functions.https.onRequest((req, res) => {

    functions.logger.log("reached send email");
    return cors(req, res, () => {
        
        // getting dest email by query string
        const dest = req.query.dest;

        const mailOptions = {
            from: 'veeran.ambalam@gmail.com', // Something like: Jane Doe <janedoe@gmail.com>
            to: dest,
            subject: 'I\'M A PICKLE!!!', // email subject
            html: `<p style="font-size: 16px;">Pickle Riiiiiiiiiiiiiiiick!!</p>
                <br />
                <img src="https://images.prod.meredith.com/product/fc8754735c8a9b4aebb786278e7265a5/1538025388228/l/rick-and-morty-pickle-rick-sticker" />
            ` // email content in HTML
        };
        
        functions.logger.log("mailOptions ready");
        // returning result
        return transporter.sendMail(mailOptions, (erro, info) => {
            functions.logger.log("transporter sent");
            if(erro){
                functions.logger.log("error: ");
                functions.logger.log(`error: ${JSON.stringify(erro)}`);
                return res.send(erro.toString());
            }
            functions.logger.log(`error: ${JSON.stringify(info)}`);
            return res.send('Sended');
        });
    });    
});



